// ======================================================
// æ–‡ç« é¡µ+å‹æƒ…é¡µï¼šå¤–éƒ¨é“¾æ¥æ–°çª—å£æ‰“å¼€ï¼ˆæ’é™¤è‡ªå·±ä¸»é¡µï¼‰
// ======================================================
document.addEventListener('DOMContentLoaded', function () {
  const currentHost = window.location.host;
  const path = window.location.pathname;
  const needPage = path.startsWith('/post/') || path === '/link.html';

  // åªæ’é™¤ã€æ—§GitHubåŸŸåã€‘ï¼Œå…¶ä»–éƒ½æ­£å¸¸åˆ¤æ–­
  const excludeHosts = [
    'xxxx.github.io'  // è¿™é‡Œåªå†™ä½ åŸæ¥çš„ github åŸŸå
  ];

  if (needPage) {
    document.querySelectorAll('a').forEach(link => {
      if (!link.href || link.href.startsWith('javascript:')) return;
      try {
        const url = new URL(link.href);
        const isExternal = url.host !== currentHost;
        const isExcluded = excludeHosts.some(h => url.host.includes(h));

        if (isExternal && !isExcluded) {
          link.target = '_blank';
          link.rel = 'noopener noreferrer';
        }
      } catch (e) {}
    });
  }
});
